mapper.select:
SELECT * FROM COUPON
ORDER BY COUPON_UID
LIMIT #{pageBegin}, #{pageScale}

SELECT *
FROM COUPON
INNER JOIN USER
ON COUPON.USER_UID = USER.USER_UID
WHERE COUPON_UID = #{COUPON_UID}
AND COUPON_NAME = #{COUPON_NAME}
AND COUPON_DATE = #{COUPON_DATE}

mapper.insert:
INSERT INTO COUPON (COUPON_FILE, ATTACHFILE_SEQ, ORIGINALFILE_NAME, PHYSICALFILE_NAME, COUPON_NAME, COUPON_DATE, COUPON_DATETIME1, COUPON_DATETIME2, USER_UID)
VALUE 

        #{attachfiles.COUPON_FILE}, #{attachfiles.ATTACHFILE_SEQ}, #{attachfiles.ORIGINALFILE_NAME}, #{attachfiles.PHYSICALFILE_NAME}, #{attachfiles.COUPON_NAME}, DATE(NOW()), #{attachfiles.COUPON_DATETIME1}, #{attachfiles.COUPON_DATETIME2}, #{USER_UID}

mapper.update:
UPDATE COUPON
SET COUPON_FILE = #{COUPON_FILE},
    COUPON_NAME = #{COUPON_NAME},
    COUPON_DATETIME1 = #{COUPON_DATETIME1},
    COUPON_DATETIME2 = #{COUPON_DATETIME2}
WHERE COUPON_UID = #{COUPON_UID}
