mapper.insert:
insert into user
( 
    user_uid
  , password
  , name
  , birth
  , gender
  , phone
  , authority
  , address
  , address_detail
  , post
)
values
(
    #{user_uid} 
  , #{password}
  , #{name}
  , #{birth}


      , '여자'


      , '남자'


  , #{phone}
  , 'USER'
  , #{address}
  , #{addressDetail}
  , #{post}
)

mapper.update:
update
    user
set 
  phone = #{phone}  

    ,password = #{password}

  , address = #{address}
  , address_detail = #{addressDetail}
  , post = #{post}
  where user_uid = #{user_uid}

mapper.select:
select
    user_uid,
    name
from user
where name = #{name}
	and phone = #{phone}

select
    user_uid
from user
where 
 user_uid = #{user_uid}
and name = #{name}
and phone = #{phone}

mapper.update:
update
    user
set 
  password = #{password}    
  where user_uid = #{user_uid}
