mapper.insert:
insert into user
( 
    user_uid
  , password
  , name
  , birth
  , gender
  , phone
  , authority
  , address
  , address_detail
  , post
)
values
(
    #{user_uid}
  , #{password}
  , #{name}
  , #{birth}
  , #{gender}
  , #{phone}
  , 'USER'
  , #{address}
  , #{addressDetail}
  , #{post}
)

mapper.update:
update
    user
set password = #{password}
  , name = #{name}
  , birth = #{birth}
  , gender = #{gender}
  , phone = #{phone}
  , authority = #{authority}
  where user_uid = #{user_uid}
