mapper.insert:
INSERT INTO USER
( 
    USER_UID
  , PASSWORD
  , NAME
  , BIRTH
  , GENDER
  , PHONE
  , AUTHORITY
  , ADDRESS
  , ADDRESS_DETAIL
  , POST
)
VALUES
(
    #{USER_UID} 
  , #{PASSWORD}
  , #{NAME}
  , #{BIRTH}


      , '여자'


      , '남자'


  , #{PHONE}
  , 'USER'
  , #{ADDRESS}
  , #{ADDRESSDETAIL}
  , #{POST}
)

mapper.update:
UPDATE
    USER
SET 
  PHONE = #{PHONE}  

    ,PASSWORD = #{PASSWORD}

  , ADDRESS = #{ADDRESS}
  , ADDRESS_DETAIL = #{ADDRESSDETAIL}
  , POST = #{POST}
  WHERE USER_UID = #{USER_UID}

mapper.select:
SELECT
    NAME
, USER_UID
FROM USER
WHERE NAME = #{NAME}
	AND PHONE = #{PHONE}

SELECT
    USER_UID
FROM USER
WHERE
 USER_UID = #{USER_UID}
AND NAME = #{NAME}
AND PHONE = #{PHONE}

mapper.update:
UPDATE USER
SET
  PASSWORD = #{PASSWORD}    
  WHERE USER_UID = #{USER_UID}

UPDATE REVIEW 
SET REVIEW_TITLE = #{REVIEW_TITLE},
    REVIEW_SCOPE = #{REVIEW_SCOPE},
    REVIEW_CONTENT = #{REVIEW_CONTENT}
WHERE REVIEW_UID = #{REVIEW_UID}

UPDATE QNA 
SET QNA_TITLE = #{REVIEW_TITLE},
    QNA_CONTENT = #{REVIEW_SCOPE}
WHERE QNA_UID = #{QNA_UID}
